import { MatDialogConfig } from '@angular/material/dialog';
import { Observable } from 'rxjs';
export interface IFsPromptConfig {
    title?: string;
    template?: string;
    hint?: string;
    label?: string;
    class?: string | string[];
    commitLabel?: string;
    cancelLabel?: string;
    commitShow?: boolean;
    cancelShow?: boolean;
    values?(keyword: any): Observable<{
        name: any;
        value: any;
    }[]>;
    dialogConfig?: MatDialogConfig;
    buttons?: IFsPromptButtonConfig[];
    required?: boolean;
    autofocus?: boolean;
    default?: any;
}
export interface IFsPromptAutocompleteChipsConfig extends IFsPromptConfig {
    allowText?: boolean;
    validateText?: (string?: any) => boolean;
}
export interface IFsPromptButtonConfig {
    label?: string;
    class?: string;
    color?: string;
    cancel?: boolean;
    value?: any;
}
